import React, { Component } from 'react';
import FadeIn from 'react-fade-in';
import { Spring } from 'react-spring/renderprops';

class SocialMedia extends Component {
    render() {
        return(
            <Spring
                from={{position: 'fixed', top: 'calc(50% - 100px)', height: '200px', color: 'rgba(0, 0, 0, 0.5)', zIndex: '99', left: -200}}
                to={{left: 25}}
                config={{delay: 4000}}
            >
                {props => (
                         <table style={props} className="socialMediaMobile">
                            <tbody>
                                <tr>
                                    <td id="socialIcons">
                                        <a href="https://www.linkedin.com/in/drewgorman" target="_blank">
                                            <i className="large linkedin icon" ></i>
                                        </a>
                                    </td>
                                </tr>
                                <tr>
                                    <td id="socialIcons">
                                        <a href="https://github.com/kangadrewie" target="_blank">
                                            <i className="large github icon" ></i>
                                        </a>
                                    </td>
                                </tr>
                                <tr>
                                    <td id="socialIcons">
                                        <a href="https://www.dropbox.com/s/0k9hbls47818dti/Andrew%20Gorman%20CV.pdf?dl=0" target="_blank">
                                            <i className="large file alternate icon" ></i>
                                        </a>
                                    </td>
                                </tr>
                                <tr>
                                    <td id="socialIcons">
                                        <a href="mailto:andrewgorman101@gmail.com">
                                            <i className="large envelope icon"></i>
                                        </a>
                                    </td>
                                </tr>
                            </tbody>
                       </table>
                )}
            </Spring>
        );
    };
};

const icons = {
    position: 'fixed',
    top: 'calc(50% - 100px)',
    left: '25px',
    height: '200px',
    color: 'rgba(0, 0, 0, 0.5)',
    zIndex: '99'
}

export default SocialMedia;
