import React, { Component } from 'react';
import Typewriter from './Typewriter.js';

class SearchBar extends Component {
    postAPI = (payload) => {
        console.log(process.env.REACT_APP_WIT_API_KEY)
        const headers = {
            method: 'GET',
            headers: {
                "Authorization": `Bearer ${process.env.REACT_APP_WIT_API_KEY}`,
            },
        }

        fetch(`https://api.wit.ai/message?v=20200513&q=${payload}`,  headers)
            .then(response => {
                console.log(response.json())
            })

    }

    handleSubmit= (e) => {
        e.preventDefault() 
        console.log(e.target[0].value)
        let payload = encodeURIComponent(e.target[0].value);
        this.postAPI(payload)
    } 

    render() {
        return(
            <div style={container}>
                <table>
                    <tr>
                        <td>
                            <i className="big angle right icon"></i>
                        </td>
                        <td>
                            <Typewriter></Typewriter>
                        </td>
                    </tr>
                </table>
                <form onSubmit={this.handleSubmit}>
                    <i className="big angle right icon"></i>
                    <input style={searchBar} type="text" placeholder="Ask Me Anything, or simply say Hi!" required="text"></input>
                    <button style={searchButton}>
                        <i className="angle right icon"></i>
                    </button>
                </form>

            </div>
        )
    }
};

const container = {
    marginTop: '40px',
    transform: 'translateX(-12x)'
}

const searchBar = {
	width: '250px',
	borderRight: 'none',
	marginRight: '5px',
	padding: '5px',
	height: '50px',
	border: '1px solid rgba(0, 0, 0, 0.27)',
	backgroundColor: 'white',
	color: 'white',
	outline: 'none',
	color: '#9DBFAF',
	borderRadius: '5px 5px 5px 5px',
    mozTransition: 'all 0.3s',
	transition: 'all 0.3s',

    fontFamily: 'monospace',
    fontSize: '1.5em'
}

const searchButton = {
	width: '80px',
    height: '50px',
	border: '1px solid rgba(0, 0, 0, 0.27)',
	textAlign: 'center',
	borderRadius: '5px 5px 5px 5px',
	cursor: 'pointer',
	fontSize: '25px',
	marginLeft: '7px',

	backgroundColor: 'orange',
	color: 'white'
}

export default SearchBar;
