import React, {Component} from 'react';
import AudioRecorder from 'react-audio-recorder';

class VoiceButton extends Component {
    start = () => {
        console.log('started')
    }
    render() {
        return(
            <React.Fragment>
                <AudioRecorder
                    onRecordStart={this.start}


                ></AudioRecorder>
                <button id="actionButton" style={btnAction} className="actionButtonMobile">
                    <i style={icon} className="microphone icon"></i>
                    Ask Me Anything
                </button>
            </React.Fragment>
        )
    }
}

const icon = {}

const btnAction = {
    height: '50px',
    width: '220px',
    borderRadius: '70px',
    borderWidth: '0',
    backgroundColor: 'orange',
    marginTop: '2em',
    zIndex: '999',

    fontFamily: 'Source Sans Pro, sans-serif',
    fontSize: '1.4em',
    fontWeight: '700',
    color: 'white',
    cursor: 'pointer',
    transition: 'none'
}

export default VoiceButton;
