import React, {Component} from 'react';
import ActionButton from './btnAction.js';
import TechIcons from './TechIcons.js';
import ScrollComponenet from './ScrollButton.js';
import ScrollDown from './ScrollDownLanding.js';
import FadeIn from 'react-fade-in';


class LandingDialog extends Component {
    render() {
        return(
            <div id="home" style={container}>
                <FadeIn transitionDuration="1400">
                    <div style={bg} className="bg bgMobile"></div>
                </FadeIn>
                <div style={dialog} className="bgCaptionContainer">
                    <div>
                    <FadeIn delay="1000" transitionDuration="1000">
                        <h1 style={header} className="bgCaptionHeading">{this.props.heading}</h1>
                    </FadeIn>

                    <FadeIn delay="1500" transitionDuration="1200">
                        <p style={caption} className="bgCaptionPara">{this.props.caption}</p>
                    </FadeIn>

                    <FadeIn delay="2000" transitionDuration="1400">
                        <ActionButton btnCaption={"Get In Touch"}></ActionButton>
                    </FadeIn>

                    </div>
                </div> 
                <ScrollDown actionLocation={'#aboutme'} title={'About Me'}></ScrollDown>
                <ScrollComponenet></ScrollComponenet>
            </div>
        );
    }
}

const container = {
    minHeight: '30rem',
    position: 'relative',
     width: '100%',
    height: '100vh',
    display: 'table',
}

const bg = {
    position: 'absolute',
    display: 'table',
    width: '100%',
    height: '100vh',
    maxHeight: '100vh',
    backgroundImage: "url(" + "https://previews.dropbox.com/p/thumb/AA5f_P4FRaBFFkAkOQ0lBBctue8VlXPLBz2MbSQfBQAH2-fX4l2T9dAFFjjMjX2BBisIibPFhVRghS7jjNxyStVGVDW8lUaiAKwOLZervYPYPr1Jpa_77C-1Rnw7HSWVPA8vEKr0sb7TzE6ulgI2H6a7EV-UkogpFaCv7E2vL_DUrgpIX_oxAHZHpJe1UIgzHtt6JMkqzOkrEUFbWh2R-tPQ4j_PhoVtK1j3R6WSWQAy8Z-D6SscvXV5EXunXSD02PLMBEhzIyHyksWVKZ8AFO6j1R8hBUSw7kZT0lyPxU2CI8beEGcswfKFH3toZUA-_zm09d3Nbc7X-m9IN70MBDjHrcfvFRagqxBs0bJhaMHrjw/p.png?fv_content=true&size_mode=5" + ")",
    backgroundPosition: 'right bottom',
    backgroundOrigin: 'content-box',
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'contain',
    paddingLeft: '50%',
    backgroundAttachment: 'scroll',
    overflow: 'hidden',
}

//Mobile Devices at 1440px wide
//const mobile = {
//  paddingLeft: '0',
//  paddingTop: '35%',
//  backgroundPosition: 'center bottom'
//  }

const dialog = {
    display: 'inline-grid',
    height: '100vh',
    justifyContent: 'center',
    alignItems: 'center',
    float: 'left',
    paddingLeft: '13%'
}

const header = {
    fontSize: '6em',
    fontFamily: 'Source Sans Pro, sans-serif',
    fontWeight: '700',
    fontColor: 'grey',
    marginBottom: '0'
}

const caption = {
    width: '50%',
    height: '50px',
    fontSize: '1.2em',
    fontFamily: 'Source Sans Pro, sans-serif',
    fontWeight: '200'
}

export default LandingDialog;
