import React, { Component } from 'react';
import Typewriter from 'typewriter-effect';

let increment = 20;
let len = 0;

class SearchBar extends Component {
    constructor(props) {
        super(props);

        this.terminal = React.createRef();

        this.state = {
            nextLine: false,
            startType: false,
            rows: [],
            intent: 'null',
            searchWidth: ['20px']
        }
    }

    postAPI = (payload) => {
        const headers = {
            method: 'GET',
            headers: {
                "Authorization": `Bearer ${process.env.REACT_APP_WIT_API_KEY}`,
            },
        }

        fetch(`https://api.wit.ai/message?v=20200513&q=${payload}`,  headers)
            .then(response => {
                return response.json()
            })
            .then((data) => {
                return data.intents[0]
            })
            .then((intent) => {
                try {
                    this.props.intent(intent);
                } catch(err) {
                    this.props.intent({name: 'unknown'})
                }
            });

    }

    handleSubmit= (e) => {
        e.preventDefault() 
        let payload = encodeURIComponent(e.target[0].value);
        this.postAPI(payload)

        if (this.state.rows.length > 3) {
            this.terminal.current.deleteRow(0)
        }
        console.log(this.terminal.current.id)
        this.setState({ rows: [...this.state.rows, '0'] })
        this.setState({searchWidth: '20px'})
        let increment = 20;
    } 

    changeWidth = (event) => {
        let x = this.state.searchWidth[0]
        let value = parseInt(x.substring(0, x.length-2), 10)
        let currentLen = event.target.value.length

        if (currentLen > len) {
             increment = increment + 20
            console.log(currentLen, 'Increase', 'Width: ', increment);
            let newState = increment.toString() + 'px';
            console.log(newState)
            this.setState({ searchWidth: [newState] })
            console.log(this.state.searchWidth)
            len = event.target.value.length
        } else {
            increment = increment - 20
            console.log(currentLen, 'Decrease', 'Width: ', increment)
            let newState = increment.toString() + 'px';
            console.log(newState)
            this.setState({ searchWidth: [newState] })
            console.log(this.state.searchWidth)
            len = event.target.value.length
        }
    }

    render() {
        return(
            <div className="ui items" style={terminal} intent={this.state.intent}>
                <div className="item">
                    <div style={terminalLeft}>
                        <i className="big angle right icon"></i>
                    </div>
                    <div style={terminalMiddle}>
                        <Typewriter
                            options={{
                                cursor: '',
                            }}
                            onInit={(typewriter) => {
                                typewriter.typeString('Ask Me Anything, or simply say Hi!')
                                .callFunction(() => {
                                    this.setState({ rows: [...this.state.rows, '0'] })
                                })
                                .start();
                            }}
                        />
                    </div>
                    <div style={terminalRight}>
                        <h1 className={this.state.nextLine ? "hide": "blink cursor"}>_</h1>
                    </div>
                </div>
                {
                    this.state.rows.map((row, index) => {
                        return(
                            <div className="item">
                                <div style={terminalLeft}>
                                    <i className="big angle right icon"></i>
                                </div>
                                <div style={terminalMiddle}>
                                    <form onSubmit={this.handleSubmit} method="post">
                                        <span style={inputBox} editablecontent="true"></span>
                                        <input style={searchBar} type="text" required="text" autofocus="autofocus" onfocus="this.select()"></input>
                                    </form>
                                </div>
                                <div style={terminalRight}>
                                    <i className="window minimize outline icon blink"></i>
                                </div>
                            </div>
                        )
                    })
                }
            </div>
        )
    }
};

const terminal = {
    marginTop: '40px',
    transform: 'translateX(-10px)',
    maxWidth: '500px',
}

const inputBox = {
    width: '20px',
    fontFamily: 'monospace',
    fontSize: '1.5em',
}

const terminalRow = {
    textAlign: 'left',
    width: '100%'
}

const terminalLeft = {
    color: 'orange',
    float: 'left',
    textAlign: 'left',
    width: '25px',
    marginRight: '10px'
}

const terminalMiddle = {
    float: 'left',
    paddingTop: '3px',
    width: 'auto'
}

const terminalRight = {
    float: 'left',
    width: '25px'
}

const botRows = {
    width: '502px !important',
    minWidth: '502px !important'
}

const container = {
    marginTop: '40px',
    transform: 'translateX(-12px)'
}

const tableStyle = {
}

const searchBar = {
    position: 'absolute',
	width: '50px',
	borderRight: 'none',
	marginRight: '5px',
	padding: '0px',
	height: '100%',
	border: '0px solid rgba(0, 0, 0, 0.27)',
	backgroundColor: 'white',
	color: 'white',
	outline: 'none',
	color: 'rgba(0, 0, 0, 0.87)',
	borderRadius: '5px 5px 5px 5px',
    mozTransition: 'all 0.3s',
	transition: 'all 0.3s',
    caretColor: 'transparent',
    fontFamily: 'monospace',
    fontSize: '1.5em',
}

const searchButton = {
    visibility: 'hidden',
	width: '10px',
    height: '10px',
	border: '0px solid rgba(0, 0, 0, 0.27)',
	textAlign: 'center',
	borderRadius: '5px 5px 5px 5px',
	cursor: 'pointer',
	fontSize: '25px',
	marginLeft: '7px',

	backgroundColor: 'orange',
	color: 'white'
}

export default SearchBar;
