import React, { Component } from 'react';
import SyntaxHighlighter from 'react-syntax-highlighter';
import { tomorrow } from 'react-syntax-highlighter/dist/esm/styles/hljs';
import { Base64 } from 'js-base64';
import { AnimateOnChange } from 'react-animation';

//  Fetch Repos - https://api.github.com/users/kangadrewie/repos
//  Fetch Repo Files - https://api.github.com/repos/kangadrewie/amazoncurrencyconverter/git/trees/master
//  Return Files Content in Base64 using file url from above, for example - https://api.github.com/repos/kangadrewie/AmazonCurrencyConverter/git/blobs/f872dc0e12fa295e5aad94b3e286e915ffe218ef

class CodeBlock extends Component {
    constructor(props) {
        super(props)

    }

    render() {
        if (this.props.error == true) {
            return(
                <AnimateOnChange>
                    <div className="ui segment" style={errorContainer}>
                        <p style={errorMessage}>Unsupported File Type. To view, please visit github.com/kangadrewie</p>
                    </div>
                </AnimateOnChange>
            );
        } else if (this.props.code === ' ') {
            return(
                 <AnimateOnChange>
                    <div className="ui segment" style={errorContainer}>
                        <p style={errorMessage}>Select a file</p>
                    </div>
                </AnimateOnChange>       
            );
        } else {
            return( 
                <AnimateOnChange>
                    <div className="ui segment" style={container}>
                        <SyntaxHighlighter 
                            language="javascript"
                            showLineNumbers={true}
                            wrapLines={true}
                            style={tomorrow}>
                                {this.props.code}
                        </SyntaxHighlighter>
                    </div>
                </AnimateOnChange>
            );
        }
    }
}

const codeBlockStyle = {
    width: '50px'
}

const container = {
    position: 'absolute',
    top: '0px',
    right: '0',
    width: '100%',
    display: 'block',
    overflowX: 'auto',
    backgroundColor: '#121212',
    border: '1px solid rgba(255, 255, 255, 0.1)',
    borderBottomRightRadius: '0 !important',
    borderBottomLeftRadius: '0 !important',
    borderBottom: '0 none !important',
    boxShadow: 'none'
}

const errorContainer = {
    position: 'absolute',
    top: '0px',
    right: '0',
    width: '79%',
    minHeight: '694px',
    height: '694px',
    display: 'table',
    backgroundColor: '#121212',
    border: '1px solid rgba(255, 255, 255, 0.1)',
    borderBottomRightRadius: '0 !important',
    borderBottomLeftRadius: '0 !important',
    borderBottom: '0 none !important',
    boxShadow: 'none'

}

const errorMessage = {
    height: '650px',
    width: '100%',
    display: 'inline-grid',
    justifyContents: 'center',
    alignItems: 'center',
    textAlign: 'center',
    color: 'rgba(255, 255, 255, 0.87)',
}


export default CodeBlock;
