import React, { Component } from 'react';
import FadeIn from 'react-fade-in';
import VisibilitySensor from 'react-visibility-sensor';

class TechIcons extends Component {
    constructor(props) {
        super(props);

        this.state = {
            matches: window.matchMedia("(min-width: 768px)").matches,
            iconSize: 'big',
            tech: {
                name: '',
                exp: '',
                frameworks: []
            }
        }
    }

    componentDidMount() {
        const handler = e => this.setState({matches: e.matches});
        window.matchMedia("(min-width: 886px)").addListener(handler);
    }

    handleClick = (e) => {
        if (e.currentTarget.getAttribute('lang') === 'python') {
            this.setState({tech: {name: 'Python 3', desc: "I build both small and large scale applications in JavaScript daily for desktop, tablet, mobile or server. Combined with an in-depth understanding of UX and design means I can take applications from start to finish. I can also provide consulting and advice on solutions."}})
        }
    }

    render() {
        console.log(this.state.matches)
        return(
            <div style={container}>
                <h3 style={heading}>Familiar Technologies : </h3>
                <table>
                    <tbody>
                        <FadeIn delay="200" transitionDuration="600" style={fadeStyle} wrapperTag={"tr"} childTag={"td"}>
                            <h3 style={braces}>{'{'}</h3>
                                <i id="codeIcons" lang="python" className={this.state.iconSize + " python icon"} alt="Python3" onClick={this.handleClick}>,</i>
                                <i id="codeIcons" lang="js" className="big js icon" alt="Javascript">,</i>
                                <i id="codeIcons" lang="html" className="big html5 icon" alt="HTML5">,</i>
                                <i id="codeIcons" className="big react icon">,</i>
                                <i id="codeIcons" className="big flask icon" alt="Flask">,</i>
                                <i id="codeIcons" className="big css3 icon" alt="CSS3">,</i>
                                <i id="codeIcons" className="big aws icon" alt="AWS">,</i>
                                <i id="codeIcons" className="big git icon" alt="GIT"></i>
                            <h3 style={braces}>{'}'}</h3>
                        </FadeIn>
                    </tbody>
                </table>
            </div>
        )
    }
}

const fadeStyle = {
    display: 'none'
}

const braces = {
    fontWeight: '800',
    fontSize: '2em'
}

const p = {
    textAlign: 'center',
    opacity: '0',
    top: '10px'
}

const tableHead = {
    height: '10px'
}

const heading = {
    fontWeight: '400',
    fontFamily: 'monospace',
    paddingTop: '50px',
    paddingBottom: '20px',
    fontSize: '1em',
}

const container = {
    positon: 'relative',
}

const langDescription = {
    width: '100%'
}

export default TechIcons;
